import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MessageSquare } from 'lucide-react';
import DropZone from './components/PDFUpload/DropZone';
import DocumentSelect from './components/DocumentList/DocumentSelect';
import QuestionInput from './components/QuestionAnswer/QuestionInput';
import AnswerList from './components/AnswerList';

const API_BASE_URL = 'http://localhost:8000';

const App = () => {
  const [state, setState] = useState({
    selectedFile: null,
    documents: [],
    selectedDocument: null,
    question: '',
    answer: '',
    loading: false,
    error: null,
    message: null,
    questionHistory: [] // New state for tracking question history
  });

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/documents`);
      setState(prev => ({ ...prev, documents: response.data }));
    } catch (err) {
      handleError(err, 'fetching documents');
    }
  };

  const handleError = (err, action) => {
    console.error(`Error ${action}:`, err);
    const errorMessage = err.code === 'ERR_NETWORK'
      ? 'Cannot connect to backend server. Please ensure it is running.'
      : `Error ${action}: ${err.response?.data?.detail || err.message}`;
    setState(prev => ({ ...prev, error: errorMessage, message: null }));
  };

  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setState(prev => ({ ...prev, loading: true, error: null }));
      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      await fetchDocuments();
      setState(prev => ({
        ...prev,
        selectedFile: null,
        error: null,
        loading: false,
        message: response.data.message || 'File uploaded successfully!'
      }));
    } catch (err) {
      handleError(err, 'uploading file');
      setState(prev => ({ ...prev, loading: false }));
    }
  };

  const handleAskQuestion = async () => {
    if (!state.selectedDocument) {
      setState(prev => ({ ...prev, error: 'Please select a document first', message: null }));
      return;
    }

    if (!state.question.trim()) {
      setState(prev => ({ ...prev, error: 'Please enter a question', message: null }));
      return;
    }

    try {
      setState(prev => ({ ...prev, loading: true, error: null, message: null }));
      const response = await axios.post(`${API_BASE_URL}/ask`, {
        document_id: state.selectedDocument,
        question: state.question,
      });

      // Add new Q&A to history
      const newQA = {
        question: state.question,
        answer: response.data.answer,
        timestamp: new Date().toISOString()
      };

      setState(prev => ({
        ...prev,
        answer: response.data.answer,
        loading: false,
        message: response.data.message,
        questionHistory: [newQA, ...prev.questionHistory], // Add new Q&A to the beginning
        question: '' // Clear the question input
      }));
    } catch (err) {
      handleError(err, 'getting answer');
      setState(prev => ({ ...prev, answer: '', loading: false }));
    }
  };

  return (
    <div className="min-h-screen bg-blue-500 p-8">
      <div className="max-w-4xl mx-auto">
        {/* Navigation Bar */}
        <nav className="bg-white shadow-md py-4 mb-8">
          <div className="flex justify-between items-center px-6">
          </div>
        </nav>

        {/* Main Content */}
        <div className="bg-white rounded-lg shadow p-6">
          {/* Error Message */}
          {state.error && (
            <div className="mb-4 p-4 bg-red-50 text-red-700 rounded-lg border border-red-200">
              {state.error}
            </div>
          )}

          {/* Success Message */}
          {state.message && (
            <div className="mb-4 p-4 bg-green-50 text-green-700 rounded-lg border border-green-200">
              {state.message}
            </div>
          )}

          {/* DropZone for PDF upload */}
          <DropZone onFileSelect={handleFileSelect} loading={state.loading}>
            <div className="text-center py-6 bg-gray-100 border-dashed border-4 border-gray-300">
              <p className="text-lg text-gray-600">Drop your PDF here or click to upload</p>
            </div>
          </DropZone>

          <DocumentSelect
            documents={state.documents}
            selectedDocument={state.selectedDocument}
            onSelect={(doc) => setState(prev => ({ ...prev, selectedDocument: doc, error: null }))}
          />

          <QuestionInput
            value={state.question}
            onChange={(e) => setState(prev => ({ ...prev, question: e.target.value }))}
            onSubmit={handleAskQuestion}
            loading={state.loading}
          />

          {/* Answer History Section */}
          {state.questionHistory.length > 0 && (
            <div className="mt-8">
              <h2 className="text-xl font-semibold mb-4">Question History</h2>
              <AnswerList questions={state.questionHistory} />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
