import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MessageSquare, Upload, FileText, Search, X } from 'lucide-react';
import DropZone from './components/PDFUpload/DropZone';
import DocumentSelect from './components/DocumentList/DocumentSelect';
import QuestionInput from './components/QuestionInput';
import AnswerList from './components/AnswerList';

const API_BASE_URL = 'http://localhost:8000';

const App = () => {
  const [state, setState] = useState({
    selectedFile: null,
    documents: [],
    selectedDocument: null,
    question: '',
    answer: '',
    loading: false,
    error: null,
    successMessage: null,
    questionHistory: []
  });

  useEffect(() => {
    fetchDocuments();
  }, []);


  const fetchDocuments = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/documents`);
      if (response.data.message) {
        setState(prev => ({ 
          ...prev, 
          documents: response.data.documents || response.data,
          successMessage: response.data.message
        }));
      } else {
        setState(prev => ({ ...prev, documents: response.data }));
      }
    } catch (err) {
      handleError(err, 'fetching documents');
    }
  };

  const handleError = (err, action) => {
    console.error(`Error ${action}:`, err);
    const errorMessage = err.response?.data?.message || // Check for backend error message
      err.response?.data?.detail ||
      err.message ||
      `Error ${action}`;
    setState(prev => ({ 
      ...prev, 
      error: errorMessage,
      successMessage: null,
      loading: false 
    }));
  };

  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setState(prev => ({ 
        ...prev, 
        loading: true, 
        error: null,
        successMessage: null 
      }));

      const response = await axios.post(`${API_BASE_URL}/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      // Display backend message if available
      setState(prev => ({
        ...prev,
        selectedFile: null,
        loading: false,
        successMessage: response.data.message || 'File uploaded successfully!'
      }));

      await fetchDocuments();
    } catch (err) {
      handleError(err, 'uploading file');
    }
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();

    if (!state.selectedDocument) {
      setState(prev => ({ 
        ...prev, 
        error: 'Please select a document first',
        successMessage: null 
      }));
      return;
    }

    if (!state.question.trim()) {
      setState(prev => ({ 
        ...prev, 
        error: 'Please enter a question',
        successMessage: null 
      }));
      return;
    }

    try {
      setState(prev => ({ 
        ...prev, 
        loading: true, 
        error: null,
        successMessage: null 
      }));

      const response = await axios.post(`${API_BASE_URL}/ask`, {
        document_id: state.selectedDocument,
        question: state.question,
      });

      // Add new Q&A to history
      const newQA = {
        question: state.question,
        answer: response.data.answer,
        timestamp: new Date().toISOString()
      };

      setState(prev => ({
        ...prev,
        answer: response.data.answer,
        loading: false,
        successMessage: response.data.message, // Display backend message
        questionHistory: [newQA, ...prev.questionHistory],
        question: ''
      }));
    } catch (err) {
      handleError(err, 'getting answer');
    }
  };

  // Clear messages after 5 seconds
  useEffect(() => {
    if (state.successMessage || state.error) {
      const timer = setTimeout(() => {
        setState(prev => ({
          ...prev,
          successMessage: null,
          error: null
        }));
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [state.successMessage, state.error]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-blue-700">
      <div className="max-w-5xl mx-auto p-6">
        {/* Header */}
        <nav className="bg-white/95 backdrop-blur-sm shadow-lg rounded-xl py-4 px-6 mb-8">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-3">
              <FileText className="h-8 w-8 text-blue-600" />
              <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                Document Q&A System
              </h1>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <div className="grid gap-8 grid-cols-1">
          {/* Left Column - Upload and Question */}
          <div className="bg-white/95 backdrop-blur-sm rounded-xl shadow-lg p-6">
            {/* Messages */}
            <div className="space-y-4 mb-6">
              {state.error && (
                <div className="relative p-4 bg-red-50 text-red-700 rounded-xl border border-red-200 animate-fadeIn">
                  <div className="flex items-center gap-3">
                    <div className="flex-shrink-0">
                      <X className="h-5 w-5 text-red-400" />
                    </div>
                    <p>{state.error}</p>
                  </div>
                  <button 
                    onClick={() => setState(prev => ({ ...prev, error: null }))}
                    className="absolute top-4 right-4 text-red-400 hover:text-red-600"
                  >
                    <X className="h-4 w-4" />
                  </button>
                </div>
              )}

              {state.successMessage && (
                <div className="relative p-4 bg-green-50 text-green-700 rounded-xl border border-green-200 animate-fadeIn">
                  <div className="flex items-center gap-3">
                    <div className="flex-shrink-0">
                      <MessageSquare className="h-5 w-5 text-green-400" />
                    </div>
                    <p>{state.successMessage}</p>
                  </div>
                  <button 
                    onClick={() => setState(prev => ({ ...prev, successMessage: null }))}
                    className="absolute top-4 right-4 text-green-400 hover:text-green-600"
                  >
                    <X className="h-4 w-4" />
                  </button>
                </div>
              )}
            </div>

            {/* Upload Section */}
            <div className="mb-8">
              <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <Upload className="h-5 w-5 text-blue-600" />
                Upload Document
              </h2>
              <DropZone onFileSelect={handleFileSelect} loading={state.loading}>
                <div className="text-center py-8 bg-blue-50 border-dashed border-2 border-blue-300 rounded-xl hover:bg-blue-100 transition-colors duration-200">
                  <Upload className="h-12 w-12 text-blue-400 mx-auto mb-3" />
                  <p className="text-lg text-blue-600 font-medium">
                    Drop your PDF here or click to upload
                  </p>
                  <p className="text-sm text-blue-400 mt-2">
                    Support for PDF files
                  </p>
                </div>
              </DropZone>
            </div>

            {/* Document Selection */}
            <div className="mb-8">
              <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <FileText className="h-5 w-5 text-blue-600" />

              </h2>
              <DocumentSelect
                documents={state.documents}
                selectedDocument={state.selectedDocument}
                onSelect={(doc) => setState(prev => ({ 
                  ...prev, 
                  selectedDocument: doc, 
                  error: null,
                  successMessage: null 
                }))}
              />
            </div>

            {/* Question Input */}
            <div className="mb-8">
              <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <Search className="h-5 w-5 text-blue-600" />
                Ask a Question
              </h2>
              <QuestionInput
                value={state.question}
                onChange={(e) => setState(prev => ({ ...prev, question: e.target.value }))}
                onSubmit={handleAskQuestion}
                loading={state.loading}
              />
            </div>

            {/* Question History */}
            {state.questionHistory.length > 0 && (
              <div>
                <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
                  <MessageSquare className="h-5 w-5 text-blue-600" />
                  Question History
                </h2>
                <AnswerList questions={state.questionHistory} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
