import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MessageSquare, Upload, FileText, Search, X } from 'lucide-react';
import DropZone from './components/PDFUpload/DropZone';
import DocumentSelect from './components/DocumentList/DocumentSelect';
import QuestionInput from './components/QuestionInput';
import AnswerList from './components/AnswerList';

const API_BASE_URL = 'http://localhost:8000';

const App = () => {
  // ... (state management stays the same)

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-blue-700">
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-6">
        {/* Header */}
        <nav className="bg-white/95 backdrop-blur-sm shadow-lg rounded-xl py-3 sm:py-4 px-4 sm:px-6 mb-6 sm:mb-8">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-2 sm:gap-3">
              <FileText className="h-6 w-6 sm:h-8 sm:w-8 text-blue-600" />
              <h1 className="text-xl sm:text-2xl font-bold bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                Document Q&A System
              </h1>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <div className="grid gap-6 sm:gap-8">
          {/* Content Container */}
          <div className="bg-white/95 backdrop-blur-sm rounded-xl shadow-lg p-4 sm:p-6">
            {/* Messages */}
            <div className="space-y-4 mb-6">
              {state.error && (
                <div className="relative p-3 sm:p-4 bg-red-50 text-red-700 rounded-xl border border-red-200 animate-fadeIn">
                  <div className="flex items-center gap-3 pr-8">
                    <div className="flex-shrink-0">
                      <X className="h-5 w-5 text-red-400" />
                    </div>
                    <p className="text-sm sm:text-base">{state.error}</p>
                  </div>
                  <button 
                    onClick={() => setState(prev => ({ ...prev, error: null }))}
                    className="absolute top-3 right-3 text-red-400 hover:text-red-600"
                  >
                    <X className="h-4 w-4" />
                  </button>
                </div>
              )}

              {state.successMessage && (
                <div className="relative p-3 sm:p-4 bg-green-50 text-green-700 rounded-xl border border-green-200 animate-fadeIn">
                  <div className="flex items-center gap-3 pr-8">
                    <div className="flex-shrink-0">
                      <MessageSquare className="h-5 w-5 text-green-400" />
                    </div>
                    <p className="text-sm sm:text-base">{state.successMessage}</p>
                  </div>
                  <button 
                    onClick={() => setState(prev => ({ ...prev, successMessage: null }))}
                    className="absolute top-3 right-3 text-green-400 hover:text-green-600"
                  >
                    <X className="h-4 w-4" />
                  </button>
                </div>
              )}
            </div>

            {/* Upload Section */}
            <div className="mb-6 sm:mb-8">
              <h2 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 flex items-center gap-2">
                <Upload className="h-5 w-5 text-blue-600" />
                Upload Document
              </h2>
              <DropZone onFileSelect={handleFileSelect} loading={state.loading}>
                <div className="text-center py-6 sm:py-8 bg-blue-50 border-dashed border-2 border-blue-300 rounded-xl hover:bg-blue-100 transition-colors duration-200">
                  <Upload className="h-8 w-8 sm:h-12 sm:w-12 text-blue-400 mx-auto mb-2 sm:mb-3" />
                  <p className="text-base sm:text-lg text-blue-600 font-medium px-4">
                    Drop your PDF here or click to upload
                  </p>
                  <p className="text-xs sm:text-sm text-blue-400 mt-2">
                    Support for PDF files
                  </p>
                </div>
              </DropZone>
            </div>

            {/* Document Selection */}
            <div className="mb-6 sm:mb-8">
              <h2 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 flex items-center gap-2">
                <FileText className="h-5 w-5 text-blue-600" />
                Select Document
              </h2>
              <DocumentSelect
                documents={state.documents}
                selectedDocument={state.selectedDocument}
                onSelect={(doc) => setState(prev => ({ 
                  ...prev, 
                  selectedDocument: doc, 
                  error: null,
                  successMessage: null 
                }))}
              />
            </div>

            {/* Question Input */}
            <div className="mb-6 sm:mb-8">
              <h2 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 flex items-center gap-2">
                <Search className="h-5 w-5 text-blue-600" />
                Ask a Question
              </h2>
              <QuestionInput
                value={state.question}
                onChange={(e) => setState(prev => ({ ...prev, question: e.target.value }))}
                onSubmit={handleAskQuestion}
                loading={state.loading}
              />
            </div>

            {/* Question History */}
            {state.questionHistory.length > 0 && (
              <div>
                <h2 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4 flex items-center gap-2">
                  <MessageSquare className="h-5 w-5 text-blue-600" />
                  Question History
                </h2>
                <AnswerList questions={state.questionHistory} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
