import React from 'react';

const QuestionInput = ({ value, onChange, onSubmit, isLoading }) => {
  return (
    <form onSubmit={onSubmit} className="mb-8">
      <div className="flex gap-4">
        <input
          type="text"
          value={value}
          onChange={onChange}
          placeholder="Ask a question about it.."
          className="flex-1 p-3 border border-blue-800 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          disabled={isLoading}
        />
        <button
          type="submit"
          disabled={isLoading || !value.trim()}
          className="bg-blue-600 text-green px-6 py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50"
        >
          {isLoading ? 'Processing...' : 'Ask'}
        </button>
      </div>
    </form>
  );
};

export default QuestionInput;
