import React from 'react';

const QuestionInput = ({ value, onChange, onSubmit, isLoading, errorMessage }) => {
  return (
    <div>
      <form onSubmit={onSubmit} className="mb-8">
        <div className="flex gap-4">
          <input
            type="text"
            value={value}
            onChange={onChange}
            placeholder="Ask a question about it..."
            className="flex-1 p-3 border border-blue-800 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isLoading}
          />
          <button
            type="submit"
            disabled={isLoading || !value.trim()}
            className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50"
          >
            {isLoading ? 'Processing...' : 'Ask'}
          </button>
        </div>
      </form>

      {/* If there is an error, display it */}
      {errorMessage && (
        <div className="bg-red-100 border-l-4 border-red-500 p-4 rounded-lg mt-4">
          <p className="text-red-700 font-medium">Error:</p>
          <p className="text-red-600">{errorMessage}</p>
        </div>
      )}
    </div>
  );
};

export default QuestionInput;
